import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect, useContext } from 'react';
import { Container, Grid } from '@material-ui/core';
import Header from './components/Header';
import Board from './components/Board';
import Stats from './components/Stats';
import axios from 'axios';

const swap = (array, i, j) => {
  const temp = array[i];
  array[i] = array[j];
  array[j] = temp;
}

const shuffle = array => {
  for(let i = array.length - 1; i >= 1; i--) {
      const j = Math.floor(Math.random() * (i+1));

      swap(array, j, i)
  }
};

function App() {
  const [cards, setCards] = useState([]);
  const [height, setHeight] = useState(window.innerHeight);

  useEffect(() => {
    let imgs = [];
    const url = "https://api.pexels.com/v1/search?query=puppy&per_page=8";
    const key = "563492ad6f9170000100000165ea7564671b4e44a5ed43af8a717c07";
    axios.get(url, {
      headers: {
        Authorization: key
      },
    }).then(res => {
      const photos = res.data.photos;
      let cards = [];
      for(let i = 0; i < photos.length; i++) {
        const card = {
          img: photos[i].src.original,
        };

        const match = {
          img: photos[i].src.original,
        };

        cards.push(card);
        cards.push(match);
      }
      const shuffled = shuffle(cards[0]);
      console.log(cards[0]);
      setCards(shuffle(cards));
    }).catch(err => console.log(err));
  }, []);

  useEffect(() => {
    setHeight(window.innerHeight);
  }, []);


  return (
    <Container className="App" maxWidth="lg">
      <Header />
        <Container maxWidth="lg">
          <Grid  item container direction="column" justify="flex-start" alignItems="center">
            <Stats />
            <Board cards={cards} height={height}/>
          </Grid>
        </Container>
    </Container>
  );
}

export default App;
