import React, { useEffect, useState } from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core';
import MemoryCard from './MemoryCard';
import { ContactSupportOutlined } from '@material-ui/icons';
import { faGrinStars } from '@fortawesome/free-solid-svg-icons';

const useStyles = makeStyles((theme) => ({
    grid: {
        display: "flex",
        justifyContent: "center",
        margin: "0 auto",
        flexWrap: "wrap",
        paddingTop: "20px"
    },
    
}));

const Board = (props) => {
    const classes = useStyles();
    const cards = props.cards;
        
    const [flippedCards, setFlippedCards] = useState([]);

    const onCardClick = (id) => {
        if (flippedCards.length == 1) {
            setFlippedCards(prev => [...prev, id]);;
        } else {
            setFlippedCards([id]);
        }
    };

    const isFlipped = (id) => {
        return flippedCards.includes(id);
    }

    const isInactive = (id) => {
        return null;
    }

    const checkMatch = () => {
        const [first, sec] = flippedCards;
        if (first.id === sec.id - 1) {
            console.log("Match");
        }
        setFlippedCards([]); 
    };

    useEffect(() => {
        console.log(flippedCards);
        if (flippedCards.length == 2) {
            checkMatch();
        }
    }, [flippedCards]);

    return (
        <Container className={classes.grid}>
            <Grid item container spacing={2} style={{height: props.height/6*4}}>
                {   
                    cards.map((card, index) => {
                        return (
                            <MemoryCard 
                                onClick={onCardClick}
                                id={card.id} 
                                key={index}
                                img={card.img} 
                                cards={cards}
                                solved={card.solved}
                                isFlipped={isFlipped(flippedCards)}
                                flippedCards={flippedCards}/>
                        )
                    })
                }
            </Grid>
        </Container>
    );

};

export default Board;
