import React, { useState } from 'react';
import classnames from "classnames";
import { useSpring, animated as a } from 'react-spring'
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import { makeStyle } from '@material-ui/core';
import ReactCardFlip from 'react-card-flip';

const MemoryCard = (props) => {
    const img = props.img;
    const backImg = "https://pixabay.com/get/gb408ec9da33f4d82b874685968d2f99d3eae5cfc2ba703ba67c815b03ccd65edcd642ad7e216f3ec48804d72a577c2dc_640.png";
    const crop = "?auto=compress&cs=tinysrgb&dpr=1&fit=crop&h=150&w=150";
    const [flipped, set] = useState(false);
    const solved = props.solved;
    const isFlipped = props.isFlipped;
    const isDisabled = props.isDisabled;

    const {transform, opacity} = useSpring({
        opacity: flipped ? 1 : 0,
        transform: `perspective(600px) rotateX(${flipped ? 180 : 0}deg)`,
        config: {mass: 5, tension: 500, friction: 80},
      })

    const flipCard = () => {
        !isDisabled && !isFlipped && !solved && props.onClick(props.index);

    }
    return (
        <Grid item xs={4} sm={3} md={3} xl={3}>
            <Card id={props.id} className="c" onClick={flipCard}>
                <ReactCardFlip isFlipped={isFlipped} flipDirection="vertical">
                <CardMedia
                    component="img"
                    image={backImg }
                />
                <CardMedia
                    component="img"
                    image={img+crop}
                />
                </ReactCardFlip>
            </Card>
        </Grid>
    );
};

/*

*/
export default MemoryCard;
