import React, { useEffect, useState, useRef } from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core';
import MemoryCard from './MemoryCard';
import Stats from './Stats';
import axios from 'axios';

const swap = (array, i, j) => {
    const temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }
  
  const shuffle = array => {
    for(let i = array.length - 1; i >= 1; i--) {
        const j = Math.floor(Math.random() * (i+1));
  
        swap(array, j, i)
    }
  };

const useStyles = makeStyles((theme) => ({
    grid: {
        display: "flex",
        justifyContent: "center",
        margin: "0 auto",
        flexWrap: "wrap",
        paddingTop: "20px"
    },
    
}));

const Board = (props) => {
    const classes = useStyles();
    const cards = props.cards;
    const [game, setGame] = useState(cards);
    let timeout = useRef(null);
    let timer = null;
    const [flipCount, setFlipCount] = useState(0);
    const [time, setTime] = useState(0);
    const [flippedCards, setFlippedCards] = useState([]);
    const [solvedCards, setSolvedCards] = useState([]);
    const [disableBoard, setDisableBoard] = useState(false);

    const getImages = () => {
        axios.get("/images")
        .then(res => {
          setGame(res.data.images);
        }).catch(err => console.log(err));
    }

    const enableCards = () => {
        setDisableBoard(false);
    };

    const disableCards = () => {
        setDisableBoard(true);
    };

    const onCardClick = (index) => {
        if (timer = null) {
            timer.setInterval(() => {
                setTime(time + 1)
            }, 1000);
        }

        if (flippedCards.length == 1) {
            setFlippedCards(prev => [...prev, index]);
            disableCards();
        } else {
            clearTimeout(timeout.current);
            setFlippedCards([index]);
        }
        setFlipCount(flipCount + 1);
    };

    const isFlipped = (index) => {
        return flippedCards.includes(index);
    }

    const checkMatch = () => {
        const [firstIdx, secondIdx] = flippedCards;
        enableCards();
        if (cards[firstIdx].img === cards[secondIdx].img) {
            setSolvedCards((prev) => ({ ...prev, [cards[firstIdx].img]: true }));
            setFlippedCards([]);
            return;
        }

        timeout.current = setTimeout(() => {
            setFlippedCards([])
        }, 500);

        
    };

    const isSolved = (card) => {
        return Boolean(solvedCards[card.img]);
    }

    const restartGame = () => {
        setSolvedCards([]);
        setFlippedCards([]);
        setFlipCount(0);
        shuffle(cards);
        setGame(cards);
        enableCards();
    };

    const getFlipCount = () => {
        return flipCount;
    }

    const getTime = (timeInSeconds) => {
        const seconds = time%60;
        const minutes = Math.floor(time/6);
        return minutes + " : " + seconds;
    }

    useEffect(() => {
        let timeout = null;
        if (flippedCards.length == 2) {
            checkMatch();
            timeout = setTimeout(checkMatch, 300);
        }
        return () => { clearTimeout(timeout); }
    }, [flippedCards]);

    return (
        <Container className={classes.grid}>
            <Stats flipCount={getFlipCount()} time={getTime(time)}/>
            <Grid item container spacing={2} style={{height: props.height/6*4}}>
                
                {   
                    cards.map((card, index) => {
                        return (
                            <MemoryCard 
                                onClick={onCardClick}
                                id={card.id} 
                                index={index}
                                key={index}
                                img={card.img} 
                                cards={cards}
                                isDisabled={disableBoard}
                                solved={isSolved(card)}
                                isFlipped={isFlipped(index)}/>
                        )
                    })
                }
            </Grid>
        </Container>
    );

};

export default Board;
