import React, { useEffect, useState } from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core';
import MemoryCard from './MemoryCard';

const useStyles = makeStyles((theme) => ({
    grid: {
        display: "flex",
        justifyContent: "center",
        margin: "0 auto",
        flexWrap: "wrap",
        paddingTop: "20px"
    },
    
}));

const Board = (props) => {
    const classes = useStyles();
    let pictures = props.pictures;
    let cardUrls = [];
    for(let i = 0; i < pictures.length; i++) {
        cardUrls.push(pictures[i]);
        cardUrls.push(pictures[i]);
    }
    
    const [memCards, setMemCards] = useState([]);
    const [won, setWon] = useState(false);
    const [flippedCards, setFlippedCards] = useState([]);

    const checkForMatch = () => {
        return (flippedCards[0].id === flippedCards[1].id);
    };

    const onCardClick = (e) => {
        e.preventDefault();
        console.log(e.target.id);
        //setFlippedCards(e.target.id);
        if (checkForMatch()) {
            setWon(true);
            console.log();
            console.log(flippedCards);
        }
        if (flippedCards > 2)
            setFlippedCards([]);
    }

    return (
        <Container className={classes.grid}>
        <Grid item container spacing={2} style={{height: props.height/6*4}}>
            {
                cardUrls.map((pic, index) => {
                    return (
                        <MemoryCard id={index} onClick={onCardClick}
                            url={pic.src.original} key={index}/>
                    )
                })
            }
        </Grid>
        </Container>
    );

};

export default Board;
