import React, { useState } from 'react';
import { useSpring, animated as a } from "react-spring";
import Container from '@material-ui/core/Container';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    card: {
        display: "flex",
        justifyContent: "center",
        height: "150px",
        width: "150px"
    },
    
}));

const MemoryCard = (props) => {
    const classes = useStyles();
    const url = props.url;
    const [flipped, set] = useState(false);
    const {transform, opacity} = useSpring({
        opacity: flipped ? 1 : 0,
        transform: `perspective(600px) rotateX(${flipped ? 180 : 0}deg)`,
        config: {mass: 5, tension: 500, friction: 80},
    });
    return (
        <Grid item key={url} sm={3} md={3} xl={3} >
            <Container onClick={() => {console.log(url); set(state => !state)}}>
                <a.Container className="c front" style={{ opacity: opacity.interpolate(o => 1 - o), transform }} >
                        <img src={url} />
                </a.Container>
                
                <a.Container className="c back" style={{ opacity, transform: transform.interpolate(t => `${t} rotateX(180deg)`) }}>
                </a.Container>
                
            </Container>
        </Grid>
    );
};

export default MemoryCard;
