import React, { useState } from 'react';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import { makeStyle } from '@material-ui/core';

const MemoryCard = (props) => {
    const url = props.url;
    const url2 = "https://pixabay.com/get/gb408ec9da33f4d82b874685968d2f99d3eae5cfc2ba703ba67c815b03ccd65edcd642ad7e216f3ec48804d72a577c2dc_640.png";
    const fit = "?auto=compress&cs=tinysrgb&dpr=1&fit=crop&h=150&w=150";
    const [flipped, set] = useState(false);
    return (
        <Grid item key={url} xs={4} sm={3} md={3} xl={3}>
            <Card id={url} onClick={() => set(state => !state)} className="c">
                <CardMedia
                    component="img"
                    image={flipped ? url+fit : url2}
                />
            </Card>
        </Grid>
    );
};

export default MemoryCard;
