import React, { useEffect, useState } from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core';
import MemoryCard from './MemoryCard';
import { ContactSupportOutlined } from '@material-ui/icons';
import { faGrinStars } from '@fortawesome/free-solid-svg-icons';

const useStyles = makeStyles((theme) => ({
    grid: {
        display: "flex",
        justifyContent: "center",
        margin: "0 auto",
        flexWrap: "wrap",
        paddingTop: "20px"
    },
    
}));

const Board = (props) => {
    const classes = useStyles();
    const cards = props.cards;
        
    const [flippedCards, setFlippedCards] = useState([]);
    const [solvedCards, setSolvedCards] = useState([]);

    const onCardClick = (index) => {
        if (flippedCards.length == 1) {
            setFlippedCards(prev => [...prev, index]);;
        } else {
            setFlippedCards([index]);
        }
        console.log(flippedCards);
    };

    const isFlipped = (index) => {
        return flippedCards.includes(index);
    }

    const checkMatch = () => {
        const [firstIdx, secondIdx] = flippedCards;
        if (cards[firstIdx].img === cards[secondIdx].img) {
            console.log("Match");
            setSolvedCards((prev) => ({ ...prev, [cards[firstIdx].img]: true }));
        }
        setFlippedCards([]); 
    };

    const isSolved = (card) => {
        return Boolean(solvedCards[card.img]);
    }

    useEffect(() => {
        if (flippedCards.length == 2) {
            checkMatch();
        }
    }, [flippedCards]);

    return (
        <Container className={classes.grid}>
            <Grid item container spacing={2} style={{height: props.height/6*4}}>
                {   
                    cards.map((card, index) => {
                        return (
                            <MemoryCard 
                                onClick={onCardClick}
                                id={card.id} 
                                index={index}
                                key={index}
                                img={card.img} 
                                cards={cards}
                                solved={isSolved(card)}
                                isFlipped={isFlipped(index)}/>
                        )
                    })
                }
            </Grid>
        </Container>
    );

};

export default Board;
