import React, { useState } from 'react';
import { useSpring, animated } from "react-spring";
import Container from '@material-ui/core/Container';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import { makeStyle, Box } from '@material-ui/core';

const MemoryCard = (props) => {
    const AnimCard = animated(Card);
    const url = props.url;
    const fit = "?auto=compress&cs=tinysrgb&dpr=1&fit=crop&h=150&w=150";
    const [flipped, set] = useState(false);
    const {transform, opacity} = useSpring({
        opacity: flipped ? 1 : 0,
        transform: `perspective(600px) rotateX(${flipped ? 180 : 0}deg)`,
        config: {mass: 5, tension: 500, friction: 80},
    });
    return (
        <Grid item key={url} xs={4} sm={3} md={3} xl={3}>
            <Card >
                <CardMedia>
                    component="img"
                    image={url+fit}
                </CardMedia>
            </Card>
        </Grid>
    );
};

/*<AnimCard className='c front'  style={{
                     backgroundImage: 'url(' + url + fit + ')',
                     opacity: opacity.interpolate(o => 1 - o), transform }}/> 
                <AnimCard className="c back" style={{ opacity, transform: transform.interpolate(t => `${t} rotateX(180deg)`) }}/>*/

export default MemoryCard;
