import React from 'react';
import { Grid } from '@material-ui/core';

const Stats = (props) => {
    return (
        <Grid item container className="gameStats" alignItems="stretch" style={{height: window.innerHeight/6}}>
              <Grid item xs={6}><span className="title">Puppy Memory Game</span></Grid>
              <Grid item xs={3}>{props.timer}<span className="stat"> Seconds</span></Grid>
              <Grid item xs={3}>{props.moves}<span className="stat"> Moves</span></Grid>
        </Grid>
    );
};

export default Stats;
