import React, { useState, useEffect } from 'react';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import { makeStyles, Box } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    options: {
        paddingBottom: "35px",
    },
}));

const Options = (props)  => {
  const classes = useStyles();
  const hide = props.hidden;
  const [hidden, setHidden] = useState(false);

  useEffect(() => {
    console.log("hide");
    setHidden(hide);
  }, []);

  return (
    <Box display={hidden ? "hidden" : "initial"}>
      <FormControl component="fieldset" className={classes.options}>
        <RadioGroup row aria-label="gender" name="gender1" value={props.difficulty} onChange={props.changeDifficulty}>
          <FormControlLabel value="easy" control={<Radio />} label="Easy - 8 Puppies" />
          <FormControlLabel value="medium" control={<Radio />} label="Medium - 12 Puppies" />
          <FormControlLabel value="hard" control={<Radio />} label="Hard - 16 Puppies" />
        </RadioGroup>
      </FormControl>
    </Box>
  );
}

export default Options;
