        if (!isFlipped)
            set(state => !state);
        else set(false);
    }
    return (
        <Grid item xs={4} sm={3} md={3} xl={3}>
            <Card id={props.id} className="c" onClick={() => props.onClick(props.id)}>
                <CardMedia
                    onClick={flipCard}
                    component="img"
                    image={flipped ? img+crop : backImg}
                />
            </Card>
        </Grid>
    );
};

export default MemoryCard;
