import React, { useEffect, useState } from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core';
import MemoryCard from './MemoryCard';
import Row from './Row';

const useStyles = makeStyles((theme) => ({
    grid: {
        display: "flex",
        justifyContent: "center",
        margin: "0 auto",
        flexWrap: "wrap",
        paddingTop: "20px"
    },
    
}));



const Board = (props) => {
    const classes = useStyles();
    let pictures = props.pictures;
    let rows = [];

    for(let i = 0; i < pictures.length; i += pictures.length/4) {
        let row = pictures.slice(i, i + pictures.length/4);
        rows.push(row);
    }

    return (
        <Container maxWidth="lg" position="absolute">
                    <Grid item container spacing={2} style={{height: window.innerHeight/6*4}}>
                        {
                            pictures.map((pic, index) => {
                                return (
                                    <MemoryCard id={index}
                                        url={pic.src.original} key={index}/>
                                )
                            })
                        }
                    </Grid>
        </Container>
    );

};

export default Board;
