import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect, useContext } from 'react';
import { useSpring, animated as a } from "react-spring";
import { Container, Grid } from '@material-ui/core';
import Header from './components/Header';
import Board from './components/Board';
import Stats from './components/Stats';
import axios from 'axios';

function App() {
  const [cards, setCards] = useState([]);
  const [height, setHeight] = useState(window.innerHeight);

  useEffect(() => {
    let imgs = [];
    const url = "https://api.pexels.com/v1/search?query=puppy&per_page=8";
    const key = "563492ad6f9170000100000165ea7564671b4e44a5ed43af8a717c07";
    axios.get(url, {
      headers: {
        Authorization: key
      },
    }).then(res => {
      const photos = res.data.photos;
      let cards = [];
      for(let i = 0; i < photos.length; i++) {
        const card1 = {
          id: 2*i,
          img: photos[i].src.original,
          solved: false,
        };

        const card2 = {
          id: 2*i + 1,
          img: photos[i].src.original,
          solved: false,
        };

        cards.push(card1);
        cards.push(card2);
      }
      setCards(cards);
    }).catch(err => console.log(err));
  }, []);

  useEffect(() => {
    setHeight(window.innerHeight);
  }, []);


  return (
    <Container className="App" maxWidth="lg">
      <Header />
        <Container maxWidth="lg">
          <Grid  item container direction="column" justify="flex-start" alignItems="center">
            <Stats />
            <Board cards={cards} height={height}/>
          </Grid>
        </Container>
    </Container>
  );
}

export default App;
